class Zeon_Newsletterpopup_Helper_Data extends Mage_Core_Helper_Abstract
{
    /**
     * Method used to get the config setting of the passed field.
     *
     * @param String $configName
     *
     * @return Array
     */
    public function getConfigDetails($configName)
    {
        return Mage::getStoreConfig('newsletter/subscription/' . $configName);
    }

}